function thresh = defanf( subject, condition, stimcolname, corrcolname )% DEFANF  Default datafile analysis routine%% thresh = defanf( subject, condition, stimcolname, corrcolname )% 21-Aug-98 -- created (RFM)defarg('condition','');defarg('stimcolname','STIMULUS');defarg('corrcolname','CORRECT');defarg('filterstr','');% read data	data=readdata(datafname(subject,condition));% analyzetrials=data(:,[ eval(stimcolname) eval(corrcolname) ]);defant(trials);return