function h = satplot( varargin )% SATPLOT  Speed-accuracy tradeoff plot%% h = satplot( fname )% 30-Apr-99 -- created (RFM)% get reaction-time and accuracy columnsdata=[];% step through filesfor i=1:nargin,	d=dfread(varargin{i});	% step through blocks	for j=1:size(d,2),		eval(d{j}.cmd);		data=[ data ; d{j}.lines(:,[ RT CORRECT ]) ];	endend% make RT binsrtlist=0:0.1:2;rtlistn=size(rtlist,2);% find proportion correct in each binrtplot=[];for i=1:(rtlistn-1),	d=data(find((data(:,1)>=rtlist(i))&(data(:,1)<rtlist(i+1))),:);	n=size(d,1);	if n>0,		c=sum(d(:,2));		p=c/n;		rtplot=[ rtplot ; mean(rtlist([ i i+1 ])) p sqrt(p*(1-p)/n) ];	endend% plot datah=errorbar(rtplot(:,1),rtplot(:,2),rtplot(:,3),'ro-');solid(h(2));xlabel 'Reaction time'ylabel 'Proportion correct'% clear return argument if not requestedif nargout==0,	clear hendreturn