function dfclose( varargin )% DFCLOSE  Close a data file%% dfclose( [ fid ], abortflag )% 16-Jul-98 -- created (RFM)% 25-Sep-98 -- default global file ID added (RFM)global df% decode argumentsif nargin==0,	fid=df.fid;	aborted=0;elseif nargin==1,	fid=df.fid;	aborted=varargin{1};else	fid=varargin{1};	aborted=varargin{2};end;% get timestamptimestamp=datestr(now,13);% write session-end markerif aborted,	dfprintf(fid,'%% aborted\n');enddfprintf(fid,'%% finished %s\n',timestamp);dfprintf(fid,'%% -\n\n');dfflush;fclose(fid);return