function dfcmd( varargin )% DFCMD  Write an embedded command to a datafile%% dfcmd( [ fid, ] formatstr, arg1, arg2, ... )% 25-Sep-98 -- created (RFM)global df% decode argumentsif ~ischar(varargin{1}),	fid=varargin{1};	formatstr=varargin{2};	args={ varargin{3:end} };else	fid=df.fid;	formatstr=varargin{1};	args={ varargin{2:end} };end;% write prefix and commanddfprintf(fid,[ '%% * ' formatstr '\n' ],args{:});return