function dfcols( varargin )% DFCOLS  Specify a datafile's columns%% dfcols( [ fid, ] name1, format1, name2, format2, ... )% 17-Jul-98 -- created (RFM)% 25-Sep-98 -- global default file ID and format string added (RFM)global df% decode argumentsif ~ischar(varargin{1}),	fid=varargin{1};	args={ varargin{2:end} };%	df.datacols=floor(nargin-1)/2;else	fid=df.fid;	args=varargin;%	df.datacols=floor(nargin/2);end;% construct column-recording command and format stringdf.format='';colcmd='';nargs=size(args,2);col=0;i=1;while i<=nargs,	if i<nargs,		if args{i+1}(1)=='%',			colname=args{i};			colformat=args{i+1};			i=i+2;		else			colname=args{i};			colformat='%d';			i=i+1;		end	else		colname=args{i};		colformat='%d';		i=i+1;	end	col=col+1;	colcmd=[ colcmd sprintf('%s=%d; ',colname,col) ];	df.format=[ df.format colformat '\t' ];enddf.format(end)='n';df.datacols=col;% construct column-recording command and format stringif 0,df.format=[];colcmd='';for i=1:df.datacols,	colcmd=[ colcmd sprintf('%s=%d; ',args{2*i-1},i) ];	df.format=[ df.format args{2*i} '\t' ];enddf.format(end)='n';end% write column-recording commanddfcmd(colcmd);return