function dfhead( varargin )% DFHEAD  Write configuration information to a data file%% dfhead( [ fid, ] tag, subject, rngseeds, version )% 16-Jul-98 -- created (RFM)% 25-Sep-98 -- global default file ID added (RFM)global df% decode argumentsif ~ischar(varargin{1}),	fid=varargin{1};	tag=varargin{2};	subject=varargin{3};	if nargin>=4, rngseeds=varargin{4}; else rngseeds=[]; end	if nargin>=5, version =varargin{5}; else version=[]; endelse	fid=df.fid;	tag=varargin{1};	subject=varargin{2};	if nargin>=3, rngseeds=varargin{3}; else rngseeds=[]; end	if nargin>=4, version =varargin{4}; else version=[]; endend% get parameters from stimulus generation libraryif exist('getconfig.m','file'),	monitor=getconfig('monitor');	viewdist=getconfig('viewdistance');	machine=getconfig('machine');else	monitor=[];end% get timestamptimestamp=datestr(now);% write headerdfprintf(fid,'%% %s\n',tag);dfprintf(fid,'%% * SUBJECT=''%s''; TIME=''%s'';\n',subject,timestamp);if ~isempty(rngseeds),	dfprintf(fid,'%% * RSEED=%.0f; RNSEED=%.0f;\n',rngseeds);endif ~isempty(version),	dfprintf(fid,'%% * VERSION=%.4f;\n',version);endif ~isempty(monitor),	dfprintf(fid,'%% * MACHINE=''%s''; MONITOR=''%s''; FPS=%.1f; PIXELSIZE=%.6f; VIEWDIST=%.2f;\n', ...		machine,monitor.name,monitor.fps,monitor.pixelsize,viewdist);endreturn