function dfline( varargin )% DFLINE  Record a line of data%% dfline( [ fid, ] col1, col2, ... )% 17-Jul-98 -- created (RFM)% 25-Sep-98 -- global default file ID and format string added (RFM)global df% decode argumentsif nargin==df.datacols,	fid=df.fid;	args=varargin;else	fid=varargin{1};	args={ varargin{2:end} };end;% write line to filedfprintf(fid,df.format,args{:});return