function fid = dfopen( fname )% DFOPEN  Open a data file%% fid = dfopen( fname )% 16-Jul-98 -- created (RFM)% 25-Sep-98 -- global default file ID added (RFM)global df% open datafile and report error if unsuccessfulfid=fopen(fname,'a');if fid==0,	error(sprintf('Unable to open datafile ''%s''',fname));enddf.fid=fid;% initialize debug flag, if necessaryif ~isfield(df,'debug'),	df.debug=[];enddfbuffer(0);% write session-begin marker to filedfprintf('\n%% +\n');return