function dfvar( varargin )% DFVAR  Write variables to a datafile as embedded commands%% dfvar( var1, var2, ... )% 20-Feb-99 -- created (RFM)global df% step through argumentscmdstr='';for i=1:nargin,	n=upper(inputname(i));	v=varargin{i};	if ischar(v),		cmdstr=[ cmdstr sprintf('%s=''%s''; ',n,v) ];	elseif prod(size(v))>1,		cmdstr=[ cmdstr sprintf('%s=%s; ',n,mat2str(v)) ];	else		cmdstr=[ cmdstr sprintf('%s=%f; ',n,v) ];	endend% write commanddfcmd(df.fid,cmdstr);return