function p = blockpause( winID, t, n )% BLOCKPAUSE  Pause partway through a block%% p = blockpause( winID, t, n )% 05-Nov-99 -- created (RFM)% 17-Dec-99 -- contents of window saved before printing message, and restored afterwards (RFM)defarg('n',100);pausenow=( (mod(t-1,n)==0) & (t~=1) );if pausenow,	winRect=screen(winID,'Rect');	offID=screen(winID,'OpenOffscreenWindow',0,winRect);	screen('CopyWindow',winID,offID,winRect,winRect,'srcCopyQuickly');	pausemsg(winID,'Press any key to continue');	screen('CopyWindow',offID,winID,winRect,winRect,'srcCopyQuickly');	screen(offID,'Close');endreturn