function key = fixpt( winID, gokeys, mouseflag, freq );% FIXPT  Show fixation point and wait for mouse click or key press.%% fixpt( winID, gokeys, mouseflag, freq );%     - <winID> is window where fixation point is shown%     - <gokeys> is list of allowable keys to be pressed%          - [] for mouse only;  default='0'%     - <mouseflag> is flag (0,1) whether to accept mouse input (default=0)%     - <freq> is frequency of 100 ms beep to play ([] for none)% set default argumentsdefarg('gokeys','0');defarg('mouseflag',0);defarg('freq','3c');global sgFGColour sgBGColour;fg = notnull( sgFGColour, 255 );bg = notnull( sgBGColour, 0   );% draw fixation pointcls(winID,bg);winRect = screen(winID,'Rect');winMid  = round(rectmid( winRect ));circRect = [ winMid-5 winMid+5 ];screen(winID,'FillOval',fg,circRect);% beep and wait for mouse click or key pressif mouseflag~=0,	flushmouse;endif isempty(freq)==0,	nicebeep(freq,100);end;while 1,	% mouse	if mouseflag~=0,		[ x y button ] = getmouse;		if button==1,			key='mouse';			break;		end	end	% key	if isempty(gokeys)==0,		if charavail==1,			key=getchar;			if isempty(find(gokeys==key))==0,				break;			end		end	endend% erase fixation pointscreen(winID,'FillRect',bg,circRect);return