function [ winID, winRect ] = screendefault( screenNum, bglum )% SCREENDEFAULT  Open screen in default mode%% [ winID, winRect ] = screendefault( screenNum )% 16-Jul-98 -- created (RFM)% set default argumentsdefarg('screenNum',0);defarg('bglum',20);% open screen[ winID, winRect ]=screen(screenNum,'OpenWindow',0,[],8);% set clutload idclut;clut(1,:)=lum2rgb(bglum);screen(winID,'SetClut',clut);% set to background colourcls(winID,0);return