function oldpath = setpath( homedir, oldflag )% SETPATH  Set path for an experiment%% oldpath = setpath( homedir )% 15-Feb-00 -- all information is stored in global variable EXPT (RFM)defarg('homedir','');defarg('oldflag',1);if nargout>0,	oldpath=path;end	if oldflag,	global cnHomeDir cnControlDir cnCodeDir cnDataDir cnAnalysisDir	cnHomeDir=ifso(isempty(homedir),pwd,homedir);	cnControlDir=sprintf('%sControl:',cnHomeDir);	cnCodeDir=sprintf('%sCode:',cnHomeDir);	cnDataDir=sprintf('%sData:',cnHomeDir);	cnAnalysisDir=sprintf('%sAnalysis:',cnHomeDir);	eval('sprintf(''addpath(''%s'')'',cnControlDir)','');	addpath(cnCodeDir,cnAnalysisDir,cnDataDir);else	global EXPT	d=ifso(isempty(homedir),pwd,homedir);	EXPT.homedir=d;	EXPT.controldir=sprintf('%sControl:',d);	EXPT.codedir=sprintf('%sCode:',d);	EXPT.datadir=sprintf('%sData:',d);	EXPT.analysisdir=sprintf('%sAnalysis:',d);	eval('sprintf(''addpath(''%s'')'',EXPT.controldir)','');	addpath(EXPT.codedir,EXPT.analysisdir,EXPT.datadir);	endreturn