function setfont( h, fname, fsize, fweight )% SETFONT  Set an object's font%% setfont( h, name, size, weight )% 05-Feb-99 -- created;  adapted from graphfont.m (RFM)% set default argumentsdefarg('fname','');defarg('fsize','');defarg('fweight','');% default settingif nargin==1,	fname='palatino';	fsize=12;	fweight='bold';end% convert font size to integerif isstr(fsize),	fsize=str2num(fsize);end% font nameif ~isempty(fname),	set(h,'FontName',fname);end% font sizeif ~isempty(fsize),	set(h,'FontSize',fsize);end% font weightif ~isempty(fweight),	set(h,'FontWeight',fweight);endreturn