function [ key, rt ] = getvalidkey( keys )% GETVALIDKEY  Wait for a keypress from a specified list of keys%% [ key, rt ] = getvalidkey( keys )% 30-Oct-97 -- created (RFM)% 27-Sep-99 -- <rt> return argument added (RFM)% record start timestarttime=clock;% flush keyboardflushkeyboard;% wait for inputwhile 1,	key=char(getchar);	if ~isempty(find(keys==key)),		break	endend% determine elapsed timert=etime(clock,starttime);return