function outseed = clockseed( inseed )% CLOCKSEED  Seed both RNGs (rand, randn) from clock, and return seeds%% outseed = clockseed( inseed )%     - outseed(1) is the seed passed to rand%     - outseed(2) is the seed passed to randn% use seeds passed as argumentsif (nargin>0) & ~isempty(inseed),	outseed=inseed;% seed rand from clock, and generate new seedselse	outseed(1)=round(1000*sum(clock));	outseed(2)=round(2000*sum(clock));	% double-seeding:  don't do this% 	rand('state',round(1000*sum(clock)));% 	randn('state',round(1000*sum(clock)));% 	outseed(1)=round((2^32)*rand);% 	outseed(2)=round((2^32)*rand);end;rand('state',outseed(1));randn('state',outseed(2));return