function etoc( varargin )global ETICTOCnow=clock;if isempty(ETICTOC),	ETICTOC.on=1;	e=NaN;else	e=1000*etime(now,ETICTOC.tic);endETICTOC.tic=now;if (nargin==1) & isstr(varargin{1}),	if strcmp(lower(varargin{1}),'on')==1,		ETICTOC.on=1;		return	elseif strcmp(lower(varargin{1}),'off')==1,		ETICTOC.on=0;		return	elseif strcmp(lower(varargin{1}),'tic')==1,		return	endendif ETICTOC.on==0,	returnendfprintf(1,'%4.0f  ',e);fprintf(1,'%s ',varargin{:});fprintf(1,'\n');return