function ok = getarchive( archive, varargin )% GETARCHIVE  Get variables from a .mat file%% ok = getarchive( archive, var1, ... )%if (~fileexist(archive)) & (~fileexist([ archive '.mat'])),if (~exist(archive,'file')) & (~exist([ archive '.mat'],'file')),	error(sprintf('Archive file ''%s'' not found',archive));endcontents=whos('-file',archive);n=size(contents,1);ok=1;for i=1:nargin-1,	found=0;	for j=1:n,		if strcmp(contents(j).name,varargin{i}),			cmd=sprintf('load(''%s'',''%s'');',archive,varargin{i});			evalin('caller',cmd,'ok=0;');			found=1;			break		end	end	if found==0,		ok=0;	end	endreturn