function ok = putarchive( archive, varargin )% PUTARCHIVE  Write variables to a .mat file%% ok = putarchive( archive, var1, ... )first=1;if (~fileexist(archive)) & (~fileexist([ archive '.mat'])),	cmd=sprintf('save(''%s'',''%s'');',archive,varargin{1});	evalin('caller',cmd);	first=2;endfor i=first:nargin-1,	cmd=sprintf('save(''%s'',''%s'',''-append'');',archive,varargin{i});	evalin('caller',cmd);endreturn