function timelapseplay( fname, n )% TIMELAPSEPLAY  Play a time-lapse movie.%% timelapseplay( fname, n )load(fname);X=size(mv,2);Y=size(mv,1);F=size(mv,3);winID=screen(0,'OpenWindow',0);hidecursor;clut=(0:255)';clut=[ clut clut clut ];screen(winID,'SetClut',clut);reel=initmovie(winID,F,[ X Y ]);filmmovie(reel,mv);playmovie(reel,10,n);freemovie(reel);showcursor;screen(winID,'Close');return