function r = normcliprnd( mu, sigma, m, n )% NORMCLIPRND  Clipped normal random number generator%% r = normcliprnd( mu, sigma, m, n )% 20-Dec-99 -- created (RFM)defarg('mu',0);defarg('sigma',1);defarg('m',1);defarg('n',1);if prod(size(m))==2,	n=m(2);	m=m(1);endr=normrnd(mu,sigma,m,n);out=( (r<=mu-2*sigma) | (r>=mu+2*sigma) );outf=find(out);outn=size(outf,1);okf=find(~out);r(outf)=r(okf(1:outn));return