function initmat = rnggrid( ndots, dim )% RNGGRID  Initial positions on a gridnaxis=ceil(sqrt(ndots));X=dim(1);Y=dim(2);xstep=floor(X/naxis);ystep=floor(Y/naxis);initmat=[];for i=1:naxis;	for j=1:naxis;		initmat = [ initmat ; xstep*i ystep*j ];	endendinitmat=initmat(randperm(size(initmat,1)),:);initmat=initmat(1:ndots,:);initmat(:,1)=initmat(:,1)-round(xstep/2);initmat(:,1)=initmat(:,1)-round((xstep/2)+random('unid',floor(xstep/1),ndots,1));initmat(:,2)=initmat(:,2)-round(ystep/2);initmat(:,2)=initmat(:,2)-round((ystep/2)+random('unid',floor(ystep/1),ndots,1));return