function rndmat = rngunif( nvect, frames, params )% RNGUNIF  Generate matrix of uniformly distributed random displacements%% rndmat = rngunif( nvect, frames, params )dispSize=params(1);angleMean=deg2rad(params(2));angleRange=deg2rad(params(3));for i=1:frames,	if angleRange~=0,		theta = random('unif',angleMean-(angleRange/2),angleMean+(angleRange/2),nvect,1);	else		theta = angleMean*ones(nvect,1);	end;	rndmat(:,:,i) = round( dispSize*[ cos(theta) -sin(theta) ] );end%mv=mean(rndmat(:,:,1));%fprintf(1,'RNGUNIF  %5.2f  ( %5.2f, %5.2f )  %5.2f\n',rad2deg(angleMean),mv(1),mv(2),rad2deg(atan2(-mv(2),mv(1))));return