function ofhv( occluder, durlist, varargin )% OFHV  Test for horizontal-vertical illusion in occluded symmetry data%% ofhv( occluder, durlist, varargin )% 25-Feb-99 -- created (RFM)% read data	vertc=0;vertn=0;horizc=0;horizn=0;for i=1:(nargin-2),		d=dfread(varargin{i});	n=size(d,2);		for j=1:n,		eval(d{j}.cmd);		if exist('LEFT','var'),			COL=LEFT;		else			COL=VERT;		end				if (strcmp(OCCLUDER,occluder)==1) & (~isempty(find(STIMDURF==durlist))),			vert  = d{j}.lines(find(d{j}.lines(:,COL)==1),CORRECT);			horiz = d{j}.lines(find(d{j}.lines(:,COL)==0),CORRECT);			vertc=vertc+sum(vert);			vertn=vertn+size(vert,1);			horizc=horizc+sum(horiz);			horizn=horizn+size(horiz,1);		end	endendvertp=vertc/vertn;horizp=horizc/horizn;fprintf(1,'vert p(c)  = %.2f\n',vertp);fprintf(1,'horiz p(c) = %.2f\n',horizp);rmat=[ horizp 1-horizp ; 1-vertp vertp ];[ dp, cp ]=sdtyn(rmat);fprintf(1,'vert bias %.2f\n',cp);return