function p = getconfig( param )% GETCONFIG  Get environment configuration parameters%% p = getconfig( param )%% valid parameters%     - init%     - pixelsize, fps%     - unit%     - viewdistance%     - fgcind, bgcind%     - monitor% 29-Apr-98 - created (RFM)% 27-Oct-99 -- modified to use single global variable ENVIRON (RFM)% 03-Nov-99 -- name changed from getstim to getconfig (RFM)global ENVIRONswitch lower(param),	case 'unit',         p=ENVIRON.unit;	case 'pixelsize',    p=ENVIRON.monitor.pixelsize;	case 'fps',          p=ENVIRON.monitor.fps;	case 'viewdistance', p=ENVIRON.viewdistance;	case 'fgcind',       p=ENVIRON.fgcind;	case 'bgcind',       p=ENVIRON.bgcind;	case 'monitor',      p=ENVIRON.monitor;	case 'stimwindow',   p=ENVIRON.stimwindow;	case 'machine',      p=ENVIRON.machine;	otherwise error( sprintf('Unknown parameter ''%s''.',param) );endreturn