function grey = lum2grey( lum, roundflag )% LUM2GREY  Convert luminances (cd/m^2) to greyscale RGB values%% grey = lum2grey( lum, roundflag )% 16-Apr-98 -- created (RFM)% 14-Apr-98 -- support created for multiple monitors (RFM)% 24-Aug-99 -- adapted for bit-stealing clut (RFM)% set default argumentsdefarg('roundflag',1);global ENVIRONif isempty(ENVIRON),	error('Stimulus generation toolbox must be initialized with ''setenv.m''');else	grey=ENVIRON.monitor.fit.sigma*power(lum-ENVIRON.monitor.fit.delta,1/ENVIRON.monitor.fit.gamma);endif roundflag,	grey=round(grey);endreturn