function [ winID, winRect, clut ] = openwin( bglum, greyonly )% OPENWIN  Open the default stimulus window%% [ winID, winRect, clut ] = openwin( bglum, greyonly )% 05-Nov-99 -- created (RFM)% set default argumentsdefarg('bglum',35);defarg('greyonly',1);[winID,winRect]=screen(getconfig('stimwindow'),'OpenWindow',0);clut=setclut(winID,bglum,'','',greyonly);writeclut;screen(winID,'Preference','WaitForVBLInterrupt',1);hidecursor;return