function newclut = setclut( winID, bglum, minlum, maxlum, greyonly )% SETCLUT  Set several clut parameters%% newclut = setclut( winID, bglum, minlum, maxlum, greyonly )% 27-Oct-99 -- created (RFM)defarg('winID','');defarg('bglum','');defarg('minlum','');defarg('maxlum','');defarg('greyonly',1);setclutwin(winID);setclutbg(bglum,greyonly);newclut=setclutrange(minlum,maxlum,greyonly);return