function newclut = setclutbg( bglum, greyonly )% SETCLUTBG  Set the clut's background luminance%% newclut = setclutbg( bglum )% 24-Aug-99 -- created (RFM)% set default argumentsdefarg('greyonly',0);global CLUT% initialize clut if necessaryif ~isfield(CLUT,'mat'),	CLUT.mat=zeros(256,3);end% set background luminanceCLUT.bglum=bglum;if greyonly,	CLUT.mat(1,:)=lum2grey(bglum);else	CLUT.mat(1,:)=lum2rgb(bglum);end% return clutnewclut=CLUT.mat;return