function mv = grabmovie( reel )% GRABMOVIE  Copy movie frames from windows to matrices%% mv = grabmovie( reel )% 27-Aug-98 -- created (RFM)% copy frames into matrixfor f=1:reel.nframes,%	mv(:,:,f)=rgb2lum(screen(reel.frameID(f),'GetImage'));	mv(:,:,f)=screen(reel.frameID(f),'GetImage');end% set background luminance%clut=screen(reel.destID,'GetClut');%mv(find(mv==0))=rgb2lum(clut(1,1));return