function [ pt, pi ] = playmovie( reel, ipf, n, priority, playview )% PLAYMOVIE  Copy movie frames to onscreen window%% playtime = playmovie( reel, ipf, n, priority, playview )%     - <reel> is movie reel to play%     - <ipf> is number of interrupts per frame (default=1)%     - <n> is number of times to play movie (default=1)%     - <priority> is priority at which to play movie (default=-1)%     - <playview> is rectangle of destination window in which to play%       movie (default=centered)%% See also INITMOVIE, FILMMOVIE, PLAYFRAMES, FREEMOVIE.% 30-Oct-97 -- created (RFM)% 08-Oct-99 -- rerouted through playframes.m (RFM)% set default argumentsdefarg('ipf',1);defarg('n',1);defarg('priority',-1);defarg('playview',reel.destView);% construct frame list and ipf listframelist=[ repmat((1:reel.nframes)',n,1) ; NaN ];ipflist=ipf*ones(size(framelist));ipflist(end)=1;% play movieif nargout>0,	[pt,pi]=playframes(reel,framelist,ipflist,priority,playview);else	playframes(reel,framelist,ipflist,priority,playview);endreturn