function slowmovie( reel, ipf, beep )% SLOWMOVIE  Copy movie frames to onscreen window%% slowmovie( reel, ipf, sound )%     - <reel> is movie reel to play%     - <ipf> is number of interrupts per frame (def=1)%     - <sound> is sound to play with frames%% See also INITMOVIE, FILMMOVIE, PLAYMOVIE.% 12-Jan-98 -- created (RFM)% preload functionsscreen('Windows');screen(reel.destID,'WaitVBL');for f=1:reel.nframes,	screen('CopyWindow',reel.frameID(f),reel.destID,reel.view,reel.destView);	if beep(f,2)~=0,		nicebeep(beep(f,1),beep(f,2));	end	screen(reel.destID,'WaitVBL',ipf(f));endreturn