function words = str2words( line, delims )% STR2WORDS  Separate a string into its constituent words.%% words = str2words( line, delims )%     - <line> is string to be separated into words%     - <delims> is vector of delimiting characters%     - <words> contains one row for each word in <line>%% e.g. str2words(' several   words separated   by  whitespace    ')%      ans =%      several   %      words     %      separated %      by        %      whitespace% 22-Oct-97 -- created (RFM)if nargin<2,	delims=[ ' ',  '	' ];end;writing=0;i=1;j=1;for c=1:size(line,2),		% determine whether character is a delimiter	space=0;	for k=1:size(delims,2),		if line(1,c)==delims(k),			space=1;			break;		end;	end;		if space==0,		writing=1;		words(i,j)=line(1,c);		j=j+1;	else		if writing==1,			i=i+1;			j=1;			writing=0;		end	endendreturn