function src = constim( levels, trialsperlevel, id )% CONSTIM  Make a method of constant stimuli trial source%% src = mkconstim( levels, trialsperlevel, id )% 20-Apr-98 -- created (RFM)% 27-Sep-98 -- made object-oriented (RFM)defarg('trialsperlevel',Inf);defarg('id',-1);src.levels=levels(:);               % stimulus levelssrc.trialsperlevel=trialsperlevel;  % trials per stimulus levelsrc.id=id;                          % trial source idsrc.nlevels=size(src.levels,1); % number of stimulus levelssrc.round=1;                    % current sampling roundsrc.iperm=1;                    % index of next permuted level to returnsrc.perm=randperm(src.nlevels); % permutation of levelssrc.trial=0;                    % trial counter% create objectsrc=class(src,'constim');return