function [ newsrc, level, id ] = gettrial( src )% GETTRIAL  Poll a sersrc trial source%% [ newsrc, level, id ] = gettrial( src )% 28-Jan-98 -- created (RFM)% 27-Sep-98 -- made object-oriented (RFM)id=src.id;if isdone(src),	level=NaN;else	level=src.levels(src.leveli);	src.trial=src.trial+1;	src.levtrial=src.levtrial+1;	if src.levtrial>=src.trialsperlevel,		src.levtrial=0;		src.leveli=src.leveli+1;	endendnewsrc=src;return