function src = tslist( varargin )% TSLIST  Make a list of trial sources%% src = tslist( src1, ... )% 28-Jan-98 -- created (RFM)% 27-Sep-98 -- made object-oriented (RFM)src.sources={ varargin{:} }';     % list of trial sourcessrc.nsources=nargin;              % number of trial sourcessrc.active=ones(src.nsources,1);  % 'active' flag for each sourcesrc.iperm=1;                      % permuted index of next source to pollsrc.perm=randperm(src.nsources)'; % permutation of levelssrc.prevsource=[];                % index of last source polledsrc.trial=0;                      % trial count% create objectsrc=class(src,'tslist');return