% PlotVisionThings - plot data and functions relating to vision% A - Absorption spectra ( absorp.mat )load absorp.matfigure;plot(AbsorpS(:,1),AbsorpS(:,2),'b-');axis([390,710,0,0.55]);title 'Cone Absorption Spectra'xlabel 'Wavelength (nm)'ylabel 'Proportion Absorbed';hold onplot(AbsorpM(:,1),AbsorpM(:,2),'g-');plot(AbsorpL(:,1),AbsorpL(:,2),'r-');hold off% B - Fourier transform of optical point spread function ( OpticalMTF.m )figure;fplot('opticalmtf',[-70,70]);axis([-70,70,0,1.1]);title 'Optical Modulation Transfer Function'xlabel 'Frequency (cpd)'ylabel 'Intensity'% C - CIE standard illuminantsfigure;subplot(1,2,1);d=dfread('illuma.txt',0,0);plot(d(:,1),d(:,2));xlabel 'Wavelength';title 'Illuminant A';graphfont;subplot(1,2,2);d=dfread('illumd65.txt',0,0);plot(d(:,1),d(:,2));xlabel 'Wavelength';title 'Illuminant D65';graphfont;% D - spectral sensitivity functionsfigure;d=dfread('vlambda.txt',0,0);plot(d(:,1),d(:,2),'g-');hold on;d=dfread('vprimelambda.txt',0,0);plot(d(:,1),d(:,2),'b-');legend('Vlambda','Vprimelambda');