function amp = opticalpsf( d )% OPTICALPSF - optical point-spread function%% amp = opticalpsf( d )%     - <d> is distance from origin (degrees)%     - <amp> is amplitude of PSF% constants from Campell and Gubisch (1968), via Geisler's WAVSDE softwareif 0,Weight=0.538;VarA=5.45e-5;VarB=1.16e-3;amp = Weight*exp( -2*(pi^2)*VarA*(d.^2) ) + ...	(1-Weight)*exp( -2*(pi^2)*VarB*(d.^2) );endw=0.538;msa2=1/(4*(pi^2)*5.45e-5);msb2=1/(4*(pi^2)*1.16e-3);psa2=1/(4*(pi^2)*msa2);psb2=1/(4*(pi^2)*msb2);amp = w*(1/2*sqrt(psa2))*exp(-(d.^2)./(2*psa2)) + ...	(1-w)*(1/2*sqrt(psb2))*exp(-(d.^2)./(2*psb2) );return