% icpm.m - ideal observer's contrast sensitivity functionglobal Frequency   % used by stimulus-generating routines% try several wavelengths in turnfor Lambda=4:2:24,	Frequency=1/Lambda;	unix('rm CSF*.mat'); % remove old stimulus files	% get psychometric function ( 200 trials per level )	[ Trials Alpha Beta ]=RunSDS('CSF0','CSF1',[ 0 1 2 4 8 16 32 64 100 ],200);	% save all trials and resulting fit	eval([ 'save icpm_' num2str(Lambda) ' Trials Alpha Beta' ]);end