function [ Image ] = VernImg( HSep, VSep, Intensity, HOff, VOff )% VernImg - Vernier acuity test image%% [ Image ] = VernImg( HSep, VSep, Intensity )%     HSep      - horizontal displacement of lines%     VSep      - vertical separation of lines%     Intensity - intensity of image foreground ( background=1 )ImageSize=64;% length and thickness of lines to be judgedLength=20;Thick=4;% coordinates of bottom left-hand corner of top lineRefI=round(ImageSize/2)-round(VSep/2)+VOff;RefJ=round(ImageSize/2)-round(HSep/2)-round(Thick/2)+HOff;% construct imageImage=ones(ImageSize,ImageSize);Image(RefI-(Length-1):RefI,RefJ:RefJ+(Thick-1))=Intensity*ones(Length,Thick);Image(RefI+(VSep+1):RefI+(VSep+1)+(Length-1),RefJ+HSep:RefJ+HSep+(Thick-1)) ...	=Intensity*ones(Length,Thick);return