function crunch% what kind of data?theoptions = cell2struct({'psychometric','single-condition psychometric','consistency','mixed psychometric','same + diff noise'},'name',1);[datatype,ok] = dolistdlg(theoptions,'name','What kind of analysis?',[225 75],'single');if ok,			% get the files to crunch	% only files with '.dat' extension	[files,folders] = selectmanyfiles('*.dat');	if iscell(files),		% loop through data files		datatype = datatype{1};				for i = 1:length(files),			cd(folders{i});			% type of analysis			if strcmp(datatype,'consistency'),				doconsist(files{i},folders{i},1);			elseif strcmp(datatype,'mixed psychometric'),	 			dopsymetmixed(files{i},folders{i},1,1,1,250);			elseif strcmp(datatype,'psychometric'),	 			dopsymet(files{i},folders{i},1,1,1,'b',250);			elseif strcmp(datatype,'single-condition psychometric'),	 			dopsymet(files{i},folders{i},1,1,0,'s',250);			elseif strcmp(datatype,'same + diff noise'),	 			dopsymetrepnz(files{i},folders{i},1,1,1,250);			end						clearfigs;						end	end	endreturn