function psy% psy %% fit data from data file% several formats are available% what kind of data?theoptions = cell2struct({'psychometric','consistency','mixed psychometric','mixed same & different noise','2 image classification','windowed classification image'},'name',1);[datatype,ok] = dolistdlg(theoptions,'name','What kind of data?',[250 75],'single');if ok,	if strcmp(datatype,'consistency'),		doconsist;	elseif strcmp(datatype,'mixed psychometric'),		dopsymetmixed;	elseif strcmp(datatype,'psychometric'),		dopsymet;	elseif strcmp(datatype,'mixed same & different noise'),		dorepnzconsist;	elseif strcmp(datatype,'2 image classification'),		doclass;	elseif strcmp(datatype,'windowed classification image'),		classwindow;	endendreturn