function [cal,calfile,calfolder] = getlocalddf% function [cal,calfile,calfolder] = getlocalddf%% get the calibration data% it should be stored in the local folder% if it is not, ask user for location% calibration filejg local;d = dir('*.ddf');ddfnames = {d.name};if length(ddfnames) ~= 1	[calfile,calfolder] = uigetfile('*.ddf','Where is the calibration file?');	if ~calfile,		return;	endelse	calfolder = pwd;	calfile = ddfnames{1};end[cal] = getddf(calfile,calfolder);return