function [void] = ViewRGB(Device,TestVals,Spot,calmatrix)% function [void] = ViewRGB(Device,TestVals,Spot,calmatrix)% Displays RGB combinations and the corresponding luminance values% in the LUT CALMATRIX.%% Depedencies: CenterRect%			   SCREEN, getchar, (psych toolbox)%% JMG Jan 30 1998 U of T Vision Labif isstr(TestVals)	TestVals = str2num(TestVals);end[windowPtr,Sc_Size] = screen(Device,'OpenWindow',160);Spot = CenterRect(Spot,Sc_Size);[oldFontName,oldFontNumber]=SCREEN(windowPtr,'TextFont',20);oldFontSize=SCREEN(windowPtr,'TextSize',16);oldStyle=SCREEN(windowPtr,'TextStyle',2);screen(windowPtr,'SetClut',[160 160 160],0);for i = 1:size(TestVals,1)	screen(windowPtr,'SetClut',TestVals(i,:),1);	SCREEN(windowPtr,'FillRect',0);	screen(windowPtr,'FillOval',1,Spot);	text = ['R = ',num2str(TestVals(i,1)),', G = ',num2str(TestVals(i,2)),', B = ',num2str(TestVals(i,3))];	[newX,newY] = SCREEN(windowPtr,'DrawText',text,10,20,2);	if ~isempty(calmatrix) 		[newX,newY] = SCREEN(windowPtr,'DrawText',['Luminance in Calibration File =  ',num2str(RGBtoL(calmatrix,TestVals(i,1),TestVals(i,2),TestVals(i,3)))],10,40,2);	end	[newX,newY] = SCREEN(windowPtr,'DrawText',' Hit <ESC> to quit.  Hit any other key to continue.',10,Sc_Size(4)-10,2);	key = getchar;	if abs(key) == 27		break;	endendscreen(windowPtr,'Close');return;