function [map] = hsvmap(numpts)defarg('numpts',256);% submaps = 3;% ptspermap = round(numpts/submaps);% ry = rymap(ptspermap+1);% yg = ygmap(ptspermap);% gb = gbmap(ptspermap);% proportionspcy = .33;pcr = (1-pcy)/2;pcg = (1-pcy)/2;ptsy = round(numpts.*pcy);ptsr = round(numpts.*pcr);ptsg = round(numpts.*pcg);% adjustptsy = ptsy + (numpts-ptsy+ptsr+ptsg);% make mapsry = rymap(ptsr);yg = ygmap(ptsy);gb = gbmap(ptsg);% combinemap = [ry;yg;gb];return;