function checkstr(varargin)% checks to see if passed arguments are strings. if they% are, they are converted into numbers. This is useful% at the beginning of experiments, when numerical arguments% are often sent as strings.numarg = length(varargin);if numarg		for i = 1:numarg				currarg = varargin{i};		if ischar(currarg)			currname = inputname(i);			evalin('caller',[currname,' = str2num(',currname,');']);		end	endend