function saveigor(file,folder,varmatrix,varnames)% function saveigor(file,folder,varmatrix,varnames)%% saves data in igor text file format% 'file' and 'folder' are the file and folder% names of the data file. % varmatrix is a column-wise matrix of the % data to be saved. they must have the same number of rows% varnames is a cell array containing the desired% named for the variables, in the order that they% appear in the varmatrix.%% September 28 1998   JMGif file	ishere = exist([folder,file],'file');	fwid = fopen([folder,file],'a+');	if ~ishere			fprintf(fwid,'IGOR');	end	fprintf(fwid,'\n');		fprintf(fwid,'WAVES');	for i = 1:size(varnames,2)		fprintf(fwid,['\t',varnames{i}]);	end	fprintf(fwid,'\n');		fprintf(fwid,'BEGIN');	fprintf(fwid,'\n');		for i = 1:size(varmatrix,1)			fprintf(fwid,'%g\t',varmatrix(i,:)); 			fprintf(fwid,'\n');				 	end	fprintf(fwid,'END');	fprintf(fwid,'\n');		fclose(fwid);end