function [files,folders] = selectmanyfiles(wildcard,prompt,nfiles)defarg('wildcard','*');defarg('prompt','Select next set of files (press cancel to stop):');defarg('nfiles','multiple');over = 0;files = {};folders = {};count = 1;while ~over		[currfiles,currfolders] = selectfiles(wildcard,prompt,nfiles);			if iscell(currfiles),		files = {files{:},currfiles{:}};		for i = 1:length(currfiles),			folders{count} = currfolders;			count = count+1;		end	else		over = 1;	end				endreturn