function centerstring( winID, str, color )% CENTERSTRING  Centre a string in a graphics window.%global sgFGColour;%fg = notnull( sgFGColour, 190 );defarg('color',190);oldSize=screen(winID,'TextSize',30);width=screen(winID,'TextWidth',str);mid = rectmid(screen(winID,'Rect'));screen(winID,'DrawText',str,round(mid(1)-(width/2)),mid(2),color);screen(winID,'TextSize',oldSize);return