function coords = getcoords(imagesz,numimages,screenrect,borderspace)%  GETCOORDS generates the coordinates for placing images within a selection window.%% Imagesz is the size of the image, numimages is the number of images, screenrect is the% dimensions of the screen specified, and borderspace is the frame that encloses the entire screen.% a rect is defined as rect = [left,top,right,bottom].defarg('borderspace',20);screenwidth = screenrect(3);screenheight = screenrect(4);if length(imagesz) == 1	imagesz(2) = imagesz(1);end% make sure they will fitif ceil(numimages/2)*imagesz(2)<screenwidth 	numtopimages = floor(numimages/2);		numbottomimages = ceil(numimages/2);		tophblankspace = floor((screenwidth-(numtopimages*imagesz(2)))/(numtopimages+1));	bothblankspace = floor((screenwidth-(numbottomimages*imagesz(2)))/(numbottomimages+1));	for i = 1:numtopimages		coords(i,:) = [tophblankspace*i+imagesz(2)*(i-1),borderspace,tophblankspace*i+imagesz(2)*(i-1) + imagesz(2),borderspace+imagesz(1)];	end	startimage=numbottomimages+1+(mod(numimages,2)*(-1));			for i = startimage:numimages		coords(i,:) = [bothblankspace*(i-startimage+1)+imagesz(2)*(i-startimage),screenheight-borderspace-imagesz(1),bothblankspace*(i-startimage+1)+imagesz(2)*(i-startimage+1),screenheight-borderspace];	end	endreturn;